            var admSegregate = admLog.split('=');
            adm[admSegregate[0]] = admSegregate[1];
        }

        new AzureNotificationValidation().inputValidation(adm); 
    }
  var apns = {};
    var apnsIn = inputs.apnsCredential;
    if (apnsIn != "") {
        var apnsNames = inputs.apnsCredential.split('^');
        for (var i = 0; i < apnsNames.length; i++) {
            var apnsLog = apnsNames[i];
            var apnsSegregate = apnsLog.split('=');
            apns[apnsSegregate[0]] = apnsSegregate[1];
        }

       new AzureNotificationValidation().inputValidation(apns); 
    }
  var baidu = {};
    var baiduIn = inputs.baiduCredential;
    if (baiduIn != "") {
        var baiduNames = inputs.baiduCredential.split('^');
        for (var i = 0; i < baiduNames.length; i++) {
            var baiduLog = baiduNames[i];
            var baiduSegregate = baiduLog.split('=');
            baidu[baiduSegregate[0]] = baiduSegregate[1];
        }

        new AzureNotificationValidation().inputValidation(baidu);  
    }
  var gcm = {};
    var gcmIn = inputs.gcmCredential;
    if (gcmIn != "") {
        var gcmNames = inputs.gcmCredential.split('^');
        for (var i = 0; i < gcmNames.length; i++) {
            var gcmLog = gcmNames[i];
            var gcmSegregate = gcmLog.split('=');
            gcm[gcmSegregate[0]] = gcmSegregate[1];
        }

        new AzureNotificationValidation().inputValidation(gcm);  
    }
    var properties = {};
    var propIn = inputs.Properties;
    if (propIn != "") {
        var propertyNames = inputs.Properties.split('^');
        for (var i = 0; i < propertyNames.length; i++) {
            var propLog = propertyNames[i];
            var propSegregate = propLog.split('=');
            properties[propSegregate[0]] = propSegregate[1];
        }
if (admIn != "") {
        properties['admCredential'] = adm;
    }
      if (propIn != "") {
        properties['apnsCredential'] = apns;
    }
      if (propIn != "") {
        properties['baiduCredential'] = baidu;
    }
      if (propIn != "") {
        properties['gcmCredential'] = gcm;
    }
       new AzureNotificationValidation().inputValidation(properties);  
    }
    var skuObj = {};
    skuObj['location'] = inputs.Location;
    if (propIn != "") {
        skuObj['properties'] = properties;
    }
    if (skuIn != "") {
        skuObj['sku'] = sku;
    }
    outputs.sku = JSON.stringify(skuObj);


})(inputs, outputs);

