sys_variable_value.value:
(function execute(inputs, outputs) {
    var obj = JSON.parse(inputs['ResponseBody']);

    if (inputs.Status == 200) {
        outputs.status = "Success";

    } else {
        outputs.status = "Error";
        outputs = new AzureNotificationHubErrorHandler().errorMessageNotificationHub(inputs, outputs);

    }
})(inputs, outputs);

